import { Component, Input, Output, EventEmitter, forwardRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR, FormsModule } from '@angular/forms';

@Component({
  selector: 'app-radio',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <label class="flex items-center cursor-pointer" [class.cursor-not-allowed]="disabled" [class.opacity-50]="disabled">
      <input
        type="radio"
        [id]="inputId"
        [name]="name"
        [value]="radioValue"
        [disabled]="disabled"
        [checked]="checked"
        (change)="onRadioChange()"
        class="w-4 h-4 rounded-full border shrink-0
               bg-white dark:bg-gray-800/30
               border-gray-300 dark:border-gray-700/60
               text-violet-500
               focus:ring-0 focus:ring-offset-0
               checked:bg-violet-500 checked:border-transparent
               disabled:bg-gray-100 disabled:border-gray-200 
               disabled:dark:bg-gray-800 disabled:dark:border-gray-700
               cursor-pointer disabled:cursor-not-allowed"
      />
      <span 
        *ngIf="label" 
        class="text-sm ml-2 text-gray-700 dark:text-gray-300"
        [class.text-gray-400]="disabled"
        [class.dark:text-gray-500]="disabled">
        {{ label }}
      </span>
    </label>
  `,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => RadioComponent),
      multi: true
    }
  ]
})
export class RadioComponent implements ControlValueAccessor {
  @Input() inputId: string = 'radio-' + Math.random().toString(36).substring(7);
  @Input() name: string = 'radio-group';
  @Input() label: string = '';
  @Input() radioValue: string | number = '';
  @Input() disabled: boolean = false;
  @Output() radioChange = new EventEmitter<string | number>();

  value: string | number = '';
  checked: boolean = false;

  onChange: (value: string | number) => void = () => {};
  onTouched: () => void = () => {};

  writeValue(value: string | number): void {
    this.value = value;
    this.checked = this.value === this.radioValue;
  }

  registerOnChange(fn: (value: string | number) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  onRadioChange(): void {
    this.value = this.radioValue;
    this.checked = true;
    this.onChange(this.radioValue);
    this.radioChange.emit(this.radioValue);
    this.onTouched();
  }
}

import { Component, Input, Output, EventEmitter, forwardRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR, FormsModule } from '@angular/forms';

export interface RadioOption {
  value: string | number;
  label: string;
  disabled?: boolean;
}

@Component({
  selector: 'app-radio-group',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div>
      <label 
        *ngIf="groupLabel" 
        class="block text-sm font-medium mb-2 text-gray-600 dark:text-gray-300">
        {{ groupLabel }}
        <span *ngIf="required" class="text-red-500">*</span>
      </label>
      <div class="flex flex-wrap items-center" [class.flex-col]="vertical" [class.items-start]="vertical" [class.gap-3]="vertical" [class.gap-6]="!vertical">
        <label 
          *ngFor="let option of options"
          class="flex items-center cursor-pointer m-3"
          [class.cursor-not-allowed]="option.disabled"
          [class.opacity-50]="option.disabled">
          <input
            type="radio"
            [name]="name"
            [value]="option.value"
            [disabled]="option.disabled"
            [checked]="selectedValue === option.value"
            (change)="onRadioChange(option.value)"
            class="w-4 h-4 rounded-full border shrink-0
                   bg-white dark:bg-gray-800/30
                   border-gray-300 dark:border-gray-700/60
                   text-violet-500
                   focus:ring-0 focus:ring-offset-0
                   checked:bg-violet-500 checked:border-transparent
                   disabled:bg-gray-100 disabled:border-gray-200 
                   disabled:dark:bg-gray-800 disabled:dark:border-gray-700
                   cursor-pointer disabled:cursor-not-allowed"
          />
          <span 
            class="text-sm ml-2 text-gray-700 dark:text-gray-300"
            [class.text-gray-400]="option.disabled"
            [class.dark:text-gray-500]="option.disabled">
            {{ option.label }}
          </span>
        </label>
      </div>
    </div>
  `,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => RadioGroupComponent),
      multi: true
    }
  ]
})
export class RadioGroupComponent implements ControlValueAccessor {
  @Input() name: string = 'radio-group-' + Math.random().toString(36).substring(7);
  @Input() groupLabel: string = '';
  @Input() options: RadioOption[] = [];
  @Input() required: boolean = false;
  @Input() vertical: boolean = false;
  @Output() selectionChange = new EventEmitter<string | number>();

  selectedValue: string | number = '';

  onChange: (value: string | number) => void = () => {};
  onTouched: () => void = () => {};

  writeValue(value: string | number): void {
    this.selectedValue = value;
  }

  registerOnChange(fn: (value: string | number) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  onRadioChange(value: string | number): void {
    this.selectedValue = value;
    this.onChange(value);
    this.selectionChange.emit(value);
    this.onTouched();
  }
}

import { Component, Input, ElementRef, ViewChild, AfterViewInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Chart, registerables } from 'chart.js';

Chart.register(...registerables);

@Component({
  selector: 'app-realtime-chart-card',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './realtime-chart-card.component.html',
  styleUrls: ['./realtime-chart-card.component.css']
})
export class RealtimeChartCardComponent implements AfterViewInit, OnDestroy {
  @Input() cardId = 'realtime-chart';
  @Input() title = 'Real Time Value';
  @Input() value = '57.81';
  @Input() deviation = 0;
  @Input() chartHeight = 248;
  @Input() tooltipText = '';
  @Input() tooltipLink = '';
  @Input() tooltipLinkText = '';

  @ViewChild('chartCanvas') chartCanvas!: ElementRef<HTMLCanvasElement>;

  private chart: Chart | null = null;
  private animationId: number | null = null;
  private dataPoints: number[] = [];

  tooltipOpen = false;

  get isPositiveDeviation(): boolean {
    return this.deviation >= 0;
  }

  get formattedDeviation(): string {
    if (this.deviation === 0) return '';
    const sign = this.deviation >= 0 ? '+' : '';
    return `${sign}${this.deviation}%`;
  }

  ngAfterViewInit(): void {
    this.initChart();
    this.startRealtimeUpdates();
  }

  ngOnDestroy(): void {
    if (this.animationId) {
      cancelAnimationFrame(this.animationId);
    }
    if (this.chart) {
      this.chart.destroy();
    }
  }

  private initChart(): void {
    if (!this.chartCanvas?.nativeElement) return;

    const ctx = this.chartCanvas.nativeElement.getContext('2d');
    if (!ctx) return;

    // Initialize with some data points
    this.dataPoints = Array.from({ length: 30 }, () => Math.random() * 40 + 35);

    // Create gradient
    const gradient = ctx.createLinearGradient(0, 0, 0, this.chartHeight);
    gradient.addColorStop(0, 'rgba(99, 102, 241, 0.2)');
    gradient.addColorStop(1, 'rgba(99, 102, 241, 0)');

    this.chart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: this.dataPoints.map((_, i) => i.toString()),
        datasets: [{
          data: this.dataPoints,
          fill: true,
          backgroundColor: gradient,
          borderColor: 'rgb(99, 102, 241)',
          borderWidth: 2,
          tension: 0.4,
          pointRadius: 0
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        animation: {
          duration: 300
        },
        plugins: {
          legend: {
            display: false
          },
          tooltip: {
            enabled: false
          }
        },
        scales: {
          x: {
            display: false
          },
          y: {
            display: false,
            min: 0,
            max: 100
          }
        }
      }
    });
  }

  private startRealtimeUpdates(): void {
    let lastUpdate = Date.now();

    const update = () => {
      const now = Date.now();
      if (now - lastUpdate >= 2000) { // Update every 2 seconds
        this.updateChart();
        lastUpdate = now;
      }
      this.animationId = requestAnimationFrame(update);
    };

    this.animationId = requestAnimationFrame(update);
  }

  private updateChart(): void {
    if (!this.chart) return;

    // Add new data point and remove oldest
    const newValue = Math.random() * 40 + 35;
    this.dataPoints.push(newValue);
    this.dataPoints.shift();

    this.chart.data.datasets[0].data = this.dataPoints;
    this.chart.update('none');

    // Update displayed value
    this.value = newValue.toFixed(2);
  }

  showTooltip(): void {
    this.tooltipOpen = true;
  }

  hideTooltip(): void {
    this.tooltipOpen = false;
  }
}

