import { Component, Input, ElementRef, ViewChild, AfterViewInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Chart, registerables } from 'chart.js';

Chart.register(...registerables);

export interface LegendItem {
  label: string;
  color: string;
}

@Component({
  selector: 'app-sales-over-time-card',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './sales-over-time-card.component.html',
  styleUrls: ['./sales-over-time-card.component.css']
})
export class SalesOverTimeCardComponent implements AfterViewInit, OnDestroy {
  @Input() cardId = 'sales-over-time';
  @Input() title = 'Sales Over Time (all stores)';
  @Input() value = '$1,482';
  @Input() changePercent = -22;
  @Input() chartHeight = 248;

  @Input() legendItems: LegendItem[] = [
    { label: 'Current', color: '#6366F1' },
    { label: 'Previous', color: '#94A3B8' }
  ];

  @ViewChild('chartCanvas') chartCanvas!: ElementRef<HTMLCanvasElement>;

  private chart: Chart | null = null;

  get isPositiveChange(): boolean {
    return this.changePercent >= 0;
  }

  get formattedChange(): string {
    const sign = this.changePercent >= 0 ? '+' : '';
    return `${sign}${this.changePercent}%`;
  }

  ngAfterViewInit(): void {
    this.initChart();
  }

  ngOnDestroy(): void {
    if (this.chart) {
      this.chart.destroy();
    }
  }

  private initChart(): void {
    if (!this.chartCanvas?.nativeElement) return;

    const ctx = this.chartCanvas.nativeElement.getContext('2d');
    if (!ctx) return;

    const labels = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    const currentData = [732, 610, 855, 790, 1100, 980, 1200, 1150, 950, 1050, 1300, 1482];
    const previousData = [900, 750, 820, 860, 950, 1020, 1100, 1000, 850, 900, 1100, 1200];

    // Create gradients
    const currentGradient = ctx.createLinearGradient(0, 0, 0, this.chartHeight);
    currentGradient.addColorStop(0, 'rgba(99, 102, 241, 0.2)');
    currentGradient.addColorStop(1, 'rgba(99, 102, 241, 0)');

    const previousGradient = ctx.createLinearGradient(0, 0, 0, this.chartHeight);
    previousGradient.addColorStop(0, 'rgba(148, 163, 184, 0.1)');
    previousGradient.addColorStop(1, 'rgba(148, 163, 184, 0)');

    this.chart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: labels,
        datasets: [
          {
            label: 'Current',
            data: currentData,
            fill: true,
            backgroundColor: currentGradient,
            borderColor: 'rgb(99, 102, 241)',
            borderWidth: 2,
            tension: 0.4,
            pointRadius: 0,
            pointHoverRadius: 4,
            pointHoverBackgroundColor: 'rgb(99, 102, 241)'
          },
          {
            label: 'Previous',
            data: previousData,
            fill: true,
            backgroundColor: previousGradient,
            borderColor: 'rgb(148, 163, 184)',
            borderWidth: 2,
            borderDash: [5, 5],
            tension: 0.4,
            pointRadius: 0,
            pointHoverRadius: 4,
            pointHoverBackgroundColor: 'rgb(148, 163, 184)'
          }
        ]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: {
            display: false
          },
          tooltip: {
            mode: 'index',
            intersect: false,
            callbacks: {
              label: (context) => `${context.dataset.label}: $${(context.parsed.y ?? 0).toLocaleString()}`
            }
          }
        },
        scales: {
          x: {
            grid: {
              display: false
            },
            ticks: {
              color: '#94a3b8'
            }
          },
          y: {
            beginAtZero: true,
            grid: {
              color: 'rgba(148, 163, 184, 0.1)'
            },
            ticks: {
              color: '#94a3b8',
              callback: (value) => '$' + value
            }
          }
        },
        interaction: {
          mode: 'nearest',
          axis: 'x',
          intersect: false
        }
      }
    });
  }
}

import { Component, Input, ElementRef, ViewChild, AfterViewInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Chart, registerables } from 'chart.js';

Chart.register(...registerables);

@Component({
  selector: 'app-sales-vs-refunds-card',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './sales-vs-refunds-card.component.html',
  styleUrls: ['./sales-vs-refunds-card.component.css']
})
export class SalesVsRefundsCardComponent implements AfterViewInit, OnDestroy {
  @Input() cardId = 'sales-vs-refunds';
  @Input() title = 'Sales VS Refunds';
  @Input() value = '+$6,796';
  @Input() changePercent = -34;
  @Input() chartHeight = 248;
  @Input() tooltipText = 'Sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit.';

  @ViewChild('chartCanvas') chartCanvas!: ElementRef<HTMLCanvasElement>;

  private chart: Chart | null = null;
  tooltipOpen = false;

  get isPositiveChange(): boolean {
    return this.changePercent >= 0;
  }

  get formattedChange(): string {
    const sign = this.changePercent >= 0 ? '+' : '';
    return `${sign}${this.changePercent}%`;
  }

  ngAfterViewInit(): void {
    this.initChart();
  }

  ngOnDestroy(): void {
    if (this.chart) {
      this.chart.destroy();
    }
  }

  private initChart(): void {
    if (!this.chartCanvas?.nativeElement) return;

    const ctx = this.chartCanvas.nativeElement.getContext('2d');
    if (!ctx) return;

    const labels = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'];
    const salesData = [4200, 3800, 5400, 4800, 4600, 5200];
    const refundsData = [-800, -600, -1200, -900, -750, -1100];

    this.chart = new Chart(ctx, {
      type: 'bar',
      data: {
        labels: labels,
        datasets: [
          {
            label: 'Sales',
            data: salesData,
            backgroundColor: 'rgb(99, 102, 241)',
            borderRadius: 4,
            barPercentage: 0.6
          },
          {
            label: 'Refunds',
            data: refundsData,
            backgroundColor: 'rgb(244, 63, 94)',
            borderRadius: 4,
            barPercentage: 0.6
          }
        ]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: {
            display: false
          },
          tooltip: {
            callbacks: {
              label: (context) => {
                const value = Math.abs(context.parsed.y ?? 0);
                return `${context.dataset.label}: $${value.toLocaleString()}`;
              }
            }
          }
        },
        scales: {
          x: {
            stacked: true,
            grid: {
              display: false
            },
            ticks: {
              color: '#94a3b8'
            }
          },
          y: {
            stacked: true,
            grid: {
              color: 'rgba(148, 163, 184, 0.1)'
            },
            ticks: {
              color: '#94a3b8',
              callback: (value) => {
                const num = value as number;
                return num >= 0 ? '$' + num : '-$' + Math.abs(num);
              }
            }
          }
        }
      }
    });
  }

  showTooltip(): void {
    this.tooltipOpen = true;
  }

  hideTooltip(): void {
    this.tooltipOpen = false;
  }
}

import { Component, Input, forwardRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR, FormsModule } from '@angular/forms';

@Component({
  selector: 'app-search-input',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div>
      <label 
        *ngIf="label" 
        [for]="inputId" 
        class="block text-sm font-medium mb-1 text-gray-600 dark:text-gray-300">
        {{ label }}
      </label>
      <div class="relative">
        <input
          [id]="inputId"
          type="search"
          [placeholder]="placeholder"
          [disabled]="disabled"
          [(ngModel)]="value"
          (ngModelChange)="onValueChange($event)"
          (blur)="onTouched()"
          (keyup.enter)="onSearch()"
          class="w-full rounded-lg border pl-9 pr-3 py-2 text-sm shadow-sm transition-colors
                 bg-white dark:bg-gray-800/30
                 border-gray-200 dark:border-gray-700/60
                 text-gray-800 dark:text-gray-100
                 placeholder:text-gray-400 dark:placeholder:text-gray-500
                 hover:border-gray-300 dark:hover:border-gray-600
                 focus:border-gray-300 dark:focus:border-gray-600 focus:outline-none focus:ring-0"
        />
        <button 
          type="button"
          (click)="onSearch()"
          class="absolute inset-y-0 left-0 flex items-center px-3 group cursor-pointer">
          <svg 
            class="w-4 h-4 text-gray-400 dark:text-gray-500 shrink-0 group-hover:text-gray-500 dark:group-hover:text-gray-400 transition-colors" 
            viewBox="0 0 16 16" 
            xmlns="http://www.w3.org/2000/svg"
            fill="currentColor">
            <path d="M7 14c-3.86 0-7-3.14-7-7s3.14-7 7-7 7 3.14 7 7-3.14 7-7 7zM7 2C4.243 2 2 4.243 2 7s2.243 5 5 5 5-2.243 5-5-2.243-5-5-5z"/>
            <path d="M15.707 14.293L13.314 11.9a8.019 8.019 0 01-1.414 1.414l2.393 2.393a.997.997 0 001.414 0 .999.999 0 000-1.414z"/>
          </svg>
        </button>
      </div>
    </div>
  `,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => SearchInputComponent),
      multi: true
    }
  ]
})
export class SearchInputComponent implements ControlValueAccessor {
  @Input() inputId: string = 'search-input-' + Math.random().toString(36).substring(7);
  @Input() label: string = '';
  @Input() placeholder: string = 'Search...';
  @Input() disabled: boolean = false;

  value: string = '';

  onChange: (value: string) => void = () => {};
  onTouched: () => void = () => {};

  writeValue(value: string): void {
    this.value = value;
  }

  registerOnChange(fn: (value: string) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  onValueChange(value: string): void {
    this.value = value;
    this.onChange(value);
  }

  onSearch(): void {
    // This can be extended with an EventEmitter for search actions
    console.log('Search triggered:', this.value);
  }
}

