          [disabled]="disabled"
          [required]="required"
          [(ngModel)]="value"
          (ngModelChange)="onValueChange($event)"
          (blur)="onTouched()"
          class="w-full rounded-lg border px-3 py-2 text-sm shadow-sm transition-colors
                 bg-white dark:bg-gray-800/30
                 border-gray-200 dark:border-gray-700/60
                 text-gray-800 dark:text-gray-100
                 placeholder:text-gray-400 dark:placeholder:text-gray-500
                 hover:border-gray-300 dark:hover:border-gray-600
                 focus:border-gray-300 dark:focus:border-gray-600 focus:outline-none focus:ring-0
                 disabled:cursor-not-allowed disabled:bg-gray-100 disabled:border-gray-200 
                 disabled:text-gray-400 disabled:dark:bg-gray-800 disabled:dark:border-gray-700 disabled:dark:text-gray-500"
          [class.pl-12]="prefix"
          [class.pl-9]="icon"
          [class.pr-8]="suffix"
          [class.border-red-400]="error"
          [class.border-green-400]="success"
          [class.py-1]="size === 'small'"
          [class.px-2]="size === 'small'"
          [class.py-3]="size === 'large'"
          [class.px-4]="size === 'large'"
        />

        <!-- Suffix -->
        <div *ngIf="suffix" class="absolute inset-y-0 right-0 flex items-center pointer-events-none">
          <span class="text-sm text-gray-400 dark:text-gray-500 px-3">{{ suffix }}</span>
        </div>
      </div>

      <!-- Supporting/Error/Success text -->
      <div *ngIf="supportingText" class="text-xs mt-1 text-gray-500 dark:text-gray-400">
        {{ supportingText }}
      </div>
      <div *ngIf="errorText" class="text-xs mt-1 text-red-500">
        {{ errorText }}
      </div>
      <div *ngIf="successText" class="text-xs mt-1 text-green-500">
        {{ successText }}
      </div>
    </div>
  `,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => TextInputComponent),
      multi: true
    }
  ]
})
export class TextInputComponent implements ControlValueAccessor {
  @Input() inputId: string = 'text-input-' + Math.random().toString(36).substring(7);
  @Input() label: string = '';
  @Input() placeholder: string = '';
  @Input() type: string = 'text';
  @Input() prefix: string = '';
  @Input() suffix: string = '';
  @Input() icon: boolean = false;
  @Input() required: boolean = false;
  @Input() disabled: boolean = false;
  @Input() supportingText: string = '';
  @Input() errorText: string = '';
  @Input() successText: string = '';
  @Input() error: boolean = false;
  @Input() success: boolean = false;
  @Input() size: 'small' | 'default' | 'large' = 'default';

  value: string = '';

  onChange: (value: string) => void = () => {};
  onTouched: () => void = () => {};

  writeValue(value: string): void {
    this.value = value;
  }

  registerOnChange(fn: (value: string) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  onValueChange(value: string): void {
    this.value = value;
    this.onChange(value);
  }
}

import { Component, Input, Output, EventEmitter, forwardRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR, FormsModule } from '@angular/forms';

@Component({
  selector: 'app-toggle-switch',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="flex items-center">
      <div class="relative select-none w-11" [class.opacity-50]="disabled">
        <input
          type="checkbox"
          [id]="inputId"
          [disabled]="disabled"
          [(ngModel)]="checked"
          (ngModelChange)="onCheckedChange($event)"
          class="sr-only peer"
        />
        <label
          [for]="inputId"
          class="block h-6 rounded-full cursor-pointer transition-colors duration-200
                 bg-gray-400 dark:bg-gray-700
                 peer-checked:bg-violet-500
                 peer-disabled:cursor-not-allowed peer-disabled:bg-gray-200 peer-disabled:border peer-disabled:border-gray-200
                 peer-disabled:dark:bg-gray-700/20 peer-disabled:dark:border-gray-700/60">
          <span 
            class="absolute block w-5 h-5 top-0.5 left-0.5 bg-white rounded-full shadow-sm transition-all duration-150 ease-out"
            [class.translate-x-5]="checked"
            [class.bg-gray-400]="disabled"
            [class.dark:bg-gray-600]="disabled"
            aria-hidden="true">
          </span>
          <span class="sr-only">{{ label || 'Toggle switch' }}</span>
        </label>
      </div>
      <div 
        *ngIf="showLabel"
        class="text-sm ml-2 italic text-gray-500 dark:text-gray-400"
        [class.text-gray-400]="disabled">
        {{ disabled ? 'Disabled' : (checked ? onLabel : offLabel) }}
      </div>
    </div>
  `,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => ToggleSwitchComponent),
      multi: true
    }
  ]
})
export class ToggleSwitchComponent implements ControlValueAccessor {
  @Input() inputId: string = 'toggle-' + Math.random().toString(36).substring(7);
  @Input() label: string = '';
  @Input() onLabel: string = 'On';
  @Input() offLabel: string = 'Off';
  @Input() showLabel: boolean = true;
  @Input() disabled: boolean = false;
  @Output() checkedChange = new EventEmitter<boolean>();

  checked: boolean = false;

  onChange: (value: boolean) => void = () => {};
  onTouched: () => void = () => {};

  writeValue(value: boolean): void {
    this.checked = value;
  }

  registerOnChange(fn: (value: boolean) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  onCheckedChange(value: boolean): void {
    this.checked = value;
    this.onChange(value);
    this.checkedChange.emit(value);
    this.onTouched();
  }
}

