.banner {
  padding: 0.75rem 1rem;
  font-size: 0.875rem;
  line-height: 1.25rem;
  border-radius: 0.375rem;
}

.banner-content {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
}

.banner-body {
  display: flex;
  align-items: flex-start;
}

.banner-icon {
  flex-shrink: 0;
  margin-right: 0.75rem;
  margin-top: 0.125rem;
  fill: currentColor;
}

.banner-message {
  flex: 1;
}

.banner-close {
  margin-left: 0.75rem;
  flex-shrink: 0;
  opacity: 0.7;
  cursor: pointer;
  background: transparent;
  border: none;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
}

.banner-close:hover {
  opacity: 1;
}

.close-icon {
  fill: currentColor;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

/* Solid Variants */
.banner-solid-warning {
  background-color: #eab308;
  color: white;
}

.banner-solid-success {
  background-color: #22c55e;
  color: white;
}

.banner-solid-error {
  background-color: #ef4444;
  color: white;
}

.banner-solid-info {
  background-color: #6366f1;
  color: white;
}

/* Soft Variants */
.banner-soft-warning {
