.nav-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  width: 100%;
  aspect-ratio: 1 / 1;
  padding: 1rem;
  border: 1px solid #e2e8f0;
  border-radius: 0.75rem;
  background-color: #ffffff;
  color: #64748b;
  font-size: 0.75rem;
  font-weight: 500;
  text-decoration: none;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.05);
  touch-action: manipulation; // Improve touch responsiveness
  -webkit-tap-highlight-color: transparent; // Remove tap highlight on mobile

  // Moderately smaller size on mobile
  @media (max-width: 767px) {
    padding: 0.75rem;
    gap: 0.375rem;
    font-size: 0.6875rem;
    border-radius: 0.625rem;
  }

  &:hover {
    background-color: #f8fafc;
    border-color: #cbd5e1;
    color: #334155;
    transform: translateY(-2px);
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 2px #e2e8f0;
  }

  &:focus-visible {
    outline: 2px solid #6366f1;
    outline-offset: 2px;
  }

  // Active state styles
  &--active {
    background-color: #eef2ff;
    border-color: #6366f1;
    color: #4f46e5;

    &:hover {
      background-color: #e0e7ff;
      border-color: #4f46e5;
      color: #4338ca;
    }

    .nav-item__icon {
      color: #4f46e5;
    }
  }
}

.nav-item__icon {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  width: 1.5rem;
  height: 1.5rem;
  color: inherit;

  // Moderately smaller icon on mobile
  @media (max-width: 767px) {
    width: 1.25rem;
    height: 1.25rem;
  }

  svg {
    width: 100%;
    height: 100%;
  }
}

.nav-item__label {
  text-align: center;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 100%;
}

// Dark mode styles
:host-context(.dark) {
  .nav-item {
    background-color: #334155;
    border-color: #475569;
    color: #cbd5e1;

    &:hover {
      background-color: #475569;
      border-color: #64748b;
      color: #f1f5f9;
    }

    &:focus {
      box-shadow: 0 0 0 2px #475569;
    }

    &:focus-visible {
      outline-color: #818cf8;
    }

    // Active state styles
    &--active {
      background-color: #312e81;
      border-color: #6366f1;
      color: #a5b4fc;

      &:hover {
        background-color: #3730a3;
        border-color: #818cf8;
        color: #c7d2fe;
      }

      .nav-item__icon {
        color: #a5b4fc;
      }
    }
  }
}

