  flex-direction: column;
  padding: 1rem;
  font-size: 0.875rem;
  line-height: 1.25rem;
  border-radius: 0.5rem;
  background-color: white;
  border: 1px solid #e5e7eb;
  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  min-width: 320px;
  max-width: 400px;
}

.notification-main {
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  width: 100%;
}

.notification-body {
  display: flex;
  align-items: flex-start;
}

.notification-icon {
  flex-shrink: 0;
  margin-right: 0.75rem;
  margin-top: 0.125rem;
  fill: currentColor;
}

.notification-content {
  flex: 1;
}

.notification-title {
  font-weight: 500;
  color: #1f2937;
  margin-bottom: 0.25rem;
}

.notification-message {
  color: #6b7280;
}

.notification-close {
  margin-left: 0.75rem;
  flex-shrink: 0;
  color: #9ca3af;
  cursor: pointer;
  background: transparent;
  border: none;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
}

.notification-close:hover {
  color: #6b7280;
}

.close-icon {
  fill: currentColor;
}

.notification-action {
  margin-top: 0.75rem;
  padding-top: 0.75rem;
  border-top: 1px solid #e5e7eb;
}

.action-link,
.action-button {
  color: #8b5cf6;
  font-weight: 500;
  text-decoration: none;
  background: transparent;
  border: none;
  padding: 0;
  cursor: pointer;
  font-size: 0.875rem;
}

.action-link:hover,
.action-button:hover {
  color: #7c3aed;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

/* Icon colors */
.icon-warning {
  color: #d97706;
}

.icon-success {
  color: #16a34a;
}

.icon-error {
  color: #dc2626;
}

.icon-info {
  color: #6366f1;
}

/* Dark mode support */
@media (prefers-color-scheme: dark) {
  .notification {
    background-color: #1f2937;
    border-color: #374151;
  }

  .notification-title {
    color: #f3f4f6;
  }

  .notification-message {
    color: #9ca3af;
  }

  .notification-close {
    color: #6b7280;
  }

  .notification-close:hover {
    color: #9ca3af;
  }

  .notification-action {
    border-color: #374151;
  }
}

