:host-context(.dark) .mobile-close-btn {
  color: #94a3b8;

  &:hover {
    background-color: #334155;
    color: #f1f5f9;
  }
}

:host-context(.dark) .logo {
  border-bottom-color: #334155;

  &__text {
    color: #f1f5f9;
  }
}

// Navigation Section
.nav-section {
  flex: 1;
  display: flex;
  flex-direction: column;

  &__label {
    display: block;
    padding: 0 0.5rem;
    margin-bottom: 0.75rem;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    color: #94a3b8;
  }

  &__list {
    list-style: none;
    margin: 0;
    padding: 0;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 0.75rem;

    // Slightly smaller gap on mobile
    @media (max-width: 767px) {
      gap: 0.5rem;
    }
  }

  &__item {
    margin: 0;
    padding: 0;

    // First item spans full width as a horizontal bar
    &:first-child {
      grid-column: 1 / -1;

      ::ng-deep .nav-item {
        flex-direction: row;
        aspect-ratio: auto;
        padding: 0.875rem 1rem;
        gap: 0.75rem;

        @media (max-width: 767px) {
          padding: 0.625rem 0.875rem;
          gap: 0.5rem;
        }
      }

      ::ng-deep .nav-item__label {
        text-align: left;
      }
    }
  }
}

:host-context(.dark) .nav-section {
  &__label {
    color: #64748b;
  }
}

