.toast {
  display: inline-flex;
  padding: 0.75rem 1rem;
  font-size: 0.875rem;
  line-height: 1.25rem;
  border-radius: 0.5rem;
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
}

.toast-content {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
}

.toast-body {
  display: flex;
  align-items: center;
}

.toast-icon {
  flex-shrink: 0;
  margin-right: 0.75rem;
  fill: currentColor;
}

.toast-message {
  flex: 1;
}

.toast-close {
  margin-left: 0.75rem;
  flex-shrink: 0;
  opacity: 0.7;
  cursor: pointer;
  background: transparent;
  border: none;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
}

.toast-close:hover {
  opacity: 1;
}

.close-icon {
  fill: currentColor;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

/* Solid Variants */
.toast-solid-warning {
  background-color: #eab308;
  color: white;
}

.toast-solid-success {
  background-color: #22c55e;
  color: white;
}

.toast-solid-error {
  background-color: #ef4444;
  color: white;
}

.toast-solid-info {
  background-color: #6366f1;
  color: white;
}

/* Soft Variants */
.toast-soft-warning {
  background-color: #fef3c7;
  border: 1px solid #fcd34d;
  color: #92400e;
}

.toast-soft-success {
  background-color: #dcfce7;
  border: 1px solid #86efac;
  color: #166534;
}

.toast-soft-error {
  background-color: #fee2e2;
  border: 1px solid #fca5a5;
  color: #991b1b;
}

.toast-soft-info {
  background-color: #e0e7ff;
  border: 1px solid #a5b4fc;
  color: #3730a3;
}

/* Outlined Variants */
.toast-outlined-warning {
  background-color: white;
  border: 1px solid #e5e7eb;
  color: #374151;
}

.toast-outlined-success {
  background-color: white;
  border: 1px solid #e5e7eb;
  color: #374151;
}

.toast-outlined-error {
  background-color: white;
  border: 1px solid #e5e7eb;
  color: #374151;
}

.toast-outlined-info {
  background-color: white;
  border: 1px solid #e5e7eb;
  color: #374151;
}

/* Icon colors for soft/outlined variants */
.icon-warning {
  color: #d97706;
}

.icon-success {
  color: #16a34a;
}

.icon-error {
  color: #dc2626;
}

.icon-info {
  color: #6366f1;
}

/* Dark mode support */
@media (prefers-color-scheme: dark) {
  .toast-outlined-warning,
  .toast-outlined-success,
  .toast-outlined-error,
  .toast-outlined-info {
    background-color: #1f2937;
    border-color: #374151;
    color: #f3f4f6;
  }
}

