import {
  Component,
  input,
  output,
  signal,
  computed,
  effect,
  ChangeDetectionStrategy,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { AlertSeverity, AlertVariant } from '../shared/alert.types';

/**
 * Toast Component
 *
 * A compact notification component for brief messages.
 * Supports different severity levels and three style variants.
 *
 * @example
 * ```html
 * <app-toast
 *   severity="success"
 *   message="Changes saved successfully!"
 *   variant="solid"
 *   [autoClose]="true"
 *   [autoCloseDelay]="3000">
 * </app-toast>
 * ```
 */
@Component({
  selector: 'app-toast',
  standalone: true,
  imports: [CommonModule],
  changeDetection: ChangeDetectionStrategy.OnPush,
  template: `
    @if (isVisible()) {
      <div role="alert" class="toast" [class]="containerClasses()">
        <div class="toast-content">
          <div class="toast-body">
            <svg 
              class="toast-icon" 
              [class]="iconClasses()" 
              width="16" 
              height="16" 
              viewBox="0 0 16 16"
            >
              @switch (severity()) {
                @case ('warning') {
                  <path d="M8 0C3.6 0 0 3.6 0 8s3.6 8 8 8 8-3.6 8-8-3.6-8-8-8zm0 12c-.6 0-1-.4-1-1s.4-1 1-1 1 .4 1 1-.4 1-1 1zm1-3H7V4h2v5z"/>
                }
                @case ('success') {
                  <path d="M8 0C3.6 0 0 3.6 0 8s3.6 8 8 8 8-3.6 8-8-3.6-8-8-8zM7 11.4L3.6 8 5 6.6l2 2 4-4L12.4 6 7 11.4z"/>
                }
                @case ('error') {
                  <path d="M8 0C3.6 0 0 3.6 0 8s3.6 8 8 8 8-3.6 8-8-3.6-8-8-8zm3.5 10.1l-1.4 1.4L8 9.4l-2.1 2.1-1.4-1.4L6.6 8 4.5 5.9l1.4-1.4L8 6.6l2.1-2.1 1.4 1.4L9.4 8l2.1 2.1z"/>
                }
                @case ('info') {
                  <path d="M8 0C3.6 0 0 3.6 0 8s3.6 8 8 8 8-3.6 8-8-3.6-8-8-8zm1 12H7V7h2v5zM8 6c-.6 0-1-.4-1-1s.4-1 1-1 1 .4 1 1-.4 1-1 1z"/>
                }
              }
            </svg>
            <div class="toast-message" [class]="messageClasses()">
              {{ message() }}
            </div>
          </div>
          @if (dismissible()) {
            <button 
              type="button"
              class="toast-close" 
              [class]="closeButtonClasses()"
              (click)="dismiss()"
              aria-label="Close toast"
            >
