  align-items: center;
  padding: 1rem 1.5rem;
  background: #f9fafb;
  border-bottom: 1px solid #e5e7eb;
}

.selection-info {
  font-size: 0.875rem;
  color: #374151;
}

.selection-info .count {
  font-weight: 600;
  color: #111827;
}

.actions {
  display: flex;
  gap: 0.5rem;
}

.btn-action {
  padding: 0.5rem 1rem;
  border: 1px solid #d1d5db;
  border-radius: 0.375rem;
  background: white;
  color: #374151;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.15s;
}

.btn-action:hover {
  background: #f9fafb;
  border-color: #9ca3af;
}

.btn-danger {
  color: #dc2626;
  border-color: #fca5a5;
}

.btn-danger:hover {
  background: #fef2f2;
  border-color: #f87171;
}

.table-wrapper {
  overflow-x: auto;
}

.transactions-table {
  width: 100%;
  border-collapse: collapse;
  font-size: 0.875rem;
}

thead {
  background: #f9fafb;
  border-bottom: 1px solid #e5e7eb;
}

th {
  padding: 0.75rem 1.5rem;
  text-align: left;
  font-size: 0.75rem;
  font-weight: 600;
  color: #6b7280;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.checkbox-col {
  width: 48px;
  padding-left: 1.5rem;
}

.text-right {
  text-align: right;
}

.checkbox-label {
  display: flex;
  align-items: center;
  cursor: pointer;
}

.checkbox-label input[type="checkbox"] {
  width: 16px;
  height: 16px;
  border: 1px solid #d1d5db;
  border-radius: 0.25rem;
  cursor: pointer;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

tbody tr {
  border-bottom: 1px solid #e5e7eb;
}

.transaction-row {
  transition: background 0.15s;
  cursor: pointer;
}

.transaction-row:hover {
  background: #f9fafb;
}

.transaction-row.selected {
  background: #eff6ff;
}

td {
  padding: 1rem 1.5rem;
}

.counterparty-info {
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.avatar {
  width: 36px;
  height: 36px;
  border-radius: 50%;
  object-fit: cover;
  flex-shrink: 0;
}

.name {
  font-weight: 500;
  color: #111827;
}

.date {
  color: #6b7280;
}

.status-badge {
  display: inline-flex;
  padding: 0.25rem 0.75rem;
  border-radius: 9999px;
  font-size: 0.75rem;
  font-weight: 500;
}

.status-badge.pending {
  background: #fef3c7;
  color: #92400e;
}

.status-badge.completed {
  background: #d1fae5;
  color: #065f46;
}

.status-badge.canceled {
  background: #fee2e2;
  color: #991b1b;
}

.status-badge.in-transit {
  background: #dbeafe;
  color: #1e40af;
}

.status-badge.ready {
  background: #e0e7ff;
  color: #3730a3;
}

.reference {
  font-weight: 500;
  color: #6366f1;
  font-family: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, monospace;
  font-size: 0.8125rem;
}

.amount {
  font-weight: 600;
  font-size: 0.875rem;
}

.amount.credit {
  color: #059669;
}

.amount.debit {
  color: #111827;
}

@media (max-width: 768px) {
  th,
  td {
    padding: 0.75rem 1rem;
  }

  .avatar {
    width: 32px;
    height: 32px;
  }

  .transactions-table {
    font-size: 0.8125rem;
  }
}

/* Dark mode styles */
:host-context(.dark) .table-container {
  background: #1e293b;
}

:host-context(.dark) .bulk-actions {
  background: #0f172a;
  border-bottom-color: #334155;
}

:host-context(.dark) .selection-info {
  color: #94a3b8;
}

:host-context(.dark) .selection-info .count {
  color: #f1f5f9;
}

:host-context(.dark) .btn-action {
  background: #1e293b;
  border-color: #475569;
  color: #e2e8f0;
}

:host-context(.dark) .btn-action:hover {
  background: #334155;
  border-color: #64748b;
}

:host-context(.dark) thead {
  background: #0f172a;
  border-bottom-color: #334155;
}

:host-context(.dark) th {
  color: #94a3b8;
}

:host-context(.dark) tbody tr {
  border-bottom-color: #334155;
}

:host-context(.dark) .transaction-row:hover {
  background: #334155;
}

:host-context(.dark) .transaction-row.selected {
  background: #1e3a5f;
}

:host-context(.dark) .name {
  color: #f1f5f9;
}

:host-context(.dark) .date {
  color: #94a3b8;
}

:host-context(.dark) .reference {
  color: #818cf8;
}

:host-context(.dark) .checkbox-label input[type="checkbox"] {
  border-color: #475569;
  background: #1e293b;
}

